package com.weatherapp.api\n\nimport com.weatherapp.model.WeatherResponse\nimport retrofit2.Response\nimport retrofit2.Retrofit\nimport retrofit2.converter.moshi.MoshiConverterFactory\nimport retrofit2.http.GET\nimport retrofit2.http.Query\n\n/**\n * Retrofit interface for the OpenWeatherMap API.\n * Defines the endpoint for fetching current weather data.\n */\ninterface WeatherApiService {\n\n    @GET("weather")\n    suspend fun getCurrentWeather(\n        @Query("q") city: String, // City name (e.g., "London")\n        @Query("appid") apiKey: String, // Your OpenWeatherMap API key\n        @Query("units") units: String = "metric" // Units for temperature (e.g., "metric" for Celsius)\n    ): Response<WeatherResponse>\n\n    companion object {\n        // Base URL for the OpenWeatherMap API\n        private const val BASE_URL = "https://api.openweathermap.org/data/2.5/"\n\n        // !!! IMPORTANT: REPLACE WITH YOUR ACTUAL OPENWEATHERMAP API KEY !!!\n        // You can get one by registering at https://openweathermap.org/api\n        const val API_KEY = "YOUR_OPENWEATHERMAP_API_KEY" // Placeholder API Key\n\n        /**\n         * Creates and provides a singleton instance of WeatherApiService.\n         */\n        fun create(): WeatherApiService {\n            val retrofit = Retrofit.Builder()\n                .baseUrl(BASE_URL)\n                .addConverterFactory(MoshiConverterFactory.create()) // Use Moshi for JSON parsing\n                .build()\n            return retrofit.create(WeatherApiService::class.java)\n        }\n    }\n}