package com.weatherapp.model\n\nimport com.squareup.moshi.Json\nimport com.squareup.moshi.JsonClass\n\n/**\n * Data classes representing the structure of the JSON response from OpenWeatherMap API.\n * Annotations like @Json and @JsonClass are used by Moshi for proper JSON parsing.\n */\n\n@JsonClass(generateAdapter = true)\ndata class WeatherResponse(\n    val coord: Coord? = null,\n    val weather: List<Weather>? = null, // List of weather conditions\n    val base: String? = null,\n    val main: Main? = null, // Main weather parameters (temperature, humidity, etc.)\n    val visibility: Int? = null,\n    val wind: Wind? = null,\n    val clouds: Clouds? = null,\n    val dt: Long? = null, // Time of data calculation, unix, UTC\n    val sys: Sys? = null,\n    val timezone: Int? = null, // Shift in seconds from UTC\n    val id: Long? = null, // City ID\n    val name: String? = null, // City name\n    val cod: Int? = null // Internal parameter\n)\n\n@JsonClass(generateAdapter = true)\ndata class Coord(\n    val lon: Double, // Longitude\n    val lat: Double // Latitude\n)\n\n@JsonClass(generateAdapter = true)\ndata class Weather(\n    val id: Int,\n    val main: String, // Group of weather parameters (Rain, Snow, Clouds etc.)\n    val description: String, // Weather condition within the group\n    val icon: String // Weather icon ID\n)\n\n@JsonClass(generateAdapter = true)\ndata class Main(\n    val temp: Double, // Temperature. Unit Default: Kelvin, Metric: Celsius, Imperial: Fahrenheit.\n    @Json(name = "feels_like") val feelsLike: Double, // Feels like temperature\n    @Json(name = "temp_min") val tempMin: Double, // Minimum temperature at the moment\n    @Json(name = "temp_max") val tempMax: Double, // Maximum temperature at the moment\n    val pressure: Int, // Atmospheric pressure (on the sea level, if there is no sea_level or grnd_level data), hPa\n    val humidity: Int // Humidity, %\n)\n\n@JsonClass(generateAdapter = true)\ndata class Wind(\n    val speed: Double, // Wind speed. Unit Default: meter/sec, Metric: meter/sec, Imperial: miles/hour.\n    val deg: Int // Wind direction, degrees (meteorological)\n)\n\n@JsonClass(generateAdapter = true)\ndata class Clouds(\n    val all: Int // Cloudiness, %\n)\n\n@JsonClass(generateAdapter = true)\ndata class Sys(\n    val type: Int? = null,\n    val id: Int? = null,\n    val country: String, // Country code (GB, JP etc.)\n    val sunrise: Long, // Sunrise time, unix, UTC\n    val sunset: Long // Sunset time, unix, UTC\n)